mapper.select:
SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM ELD_STTUS
                           WHERE LINK_ID = #{link_id}
                           ORDER BY

                                    TO_NUMBER(REGEXP_SUBSTR(eld_id, '\d+'))


                                    leakage_alarm


                                    ASC


                                    DESC

                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM ELD_ALARM_HISTORY
                           WHERE LINK_ID = #{link_id}
                           AND ALARM_DATETIME >= #{startDate}
                           AND ALARM_DATETIME <= #{endDate}
                           ORDER BY

                                    TO_NUMBER(REGEXP_SUBSTR(eld_id, '\d+'))


                                    alarm_datetime


                                    alarm_type


                                    release_datetime


                                    ASC


                                    DESC

                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}
