mapper.select:
SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM eltgnr
                           WHERE LINK_ID = #{link_id}
                           ORDER BY ELTGNR_ID
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM ELTGNR_STTUS
                           WHERE LINK_ID = #{link_id}
                           ORDER BY ELTGNR_ID
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM ELTGNR_ALARM_HISTORY
                           WHERE LINK_ID = #{link_id}
                           AND ALARM_DATETIME >= #{startDate}
                           AND ALARM_DATETIME <= #{endDate}
                           ORDER BY ELTGNR_ID
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM ELTGNR_RUN_HISTORY
                           WHERE LINK_ID = #{link_id}
                             AND RUN_START_DATETIME >= #{startDate}
                             AND RUN_START_DATETIME <= #{endDate}
                           ORDER BY ELTGNR_ID
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}
