mapper.select:
SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM entry_entr_bar
                           ORDER BY entry_entr_bar_no
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM ENTRY_ENTR_BAR_STTUS
                           ORDER BY ENTRY_ENTR_BAR_NO
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}
