mapper.select:
SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM frplg
                           where LINK_ID = #{link_id}
                           ORDER BY frplg_id
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM FRPLG_STTUS
                           where LINK_ID = #{link_id}
                           ORDER BY FRPLG_ID
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM FRPLG_ALARM_HISTORY
                           where LINK_ID = #{link_id}
                           ORDER BY FRPLG_ID
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}
