mapper.select:
SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM pow_pop
                           ORDER BY pow_pop_no
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM POW_POP_STTUS
                           ORDER BY POW_POP_NO
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}
