mapper.select:
SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM rect
                           where LINK_ID = #{link_id}
                           ORDER BY rect_id
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM RECT_STTUS
                           where LINK_ID = #{link_id}
                           ORDER BY RECT_ID
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM RECT_ALARM_HISTORY
                           where LINK_ID = #{link_id}
                             AND ALARM_DATETIME >= #{startDate}
                             AND ALARM_DATETIME <= #{endDate}
                           ORDER BY RECT_ID
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}
