mapper.select:
SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM swtbrd
                           ORDER BY swtbrd_no
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM SWTBRD_STTUS
                           ORDER BY SWTBRD_NO
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}
