mapper.select:
SELECT * FROM (
SELECT a.*, ROWNUM rnum
FROM (
SELECT * FROM SWTBRD_ALARM_HISTORY
WHERE LINK_ID = #{link_id}
AND ALARM_DATETIME >= #{startDate}
AND ALARM_DATETIME <= #{endDate}
ORDER BY

    TO_NUMBER(REGEXP_SUBSTR(swtbrd_id, '\d+'))


    alarm_datetime


    alarm_type


    release_datetime


    ASC


    DESC


) a
)
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM (
SELECT a.*, ROWNUM rnum
FROM (
SELECT * FROM SWTBRD_RUN_HISTORY
WHERE LINK_ID = #{link_id}
AND RUN_START_DATETIME >= #{startDate}
AND RUN_START_DATETIME <= #{endDate}
ORDER BY

    TO_NUMBER(REGEXP_SUBSTR(swtbrd_id, '\d+'))


    run_start_datetime


    run_end_datetime


    ASC


    DESC

) a
)
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}
