mapper.select:
SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM TEMP_HUM
                           WHERE LINK_ID = #{link_id}
                           ORDER BY TEMP_HUM_ID
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM TEMP_HUM_STTUS
                           WHERE LINK_ID = #{link_id}
                           ORDER BY TEMP_HUM_ID
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}
