mapper.select:
SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM WATER_TANK
                           WHERE LINK_ID = #{link_id}
                           ORDER BY TANK_ID
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM WATER_TANK_STTUS
                           WHERE LINK_ID = #{link_id}
                           ORDER BY TANK_ID
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM (
                  SELECT a.*, ROWNUM rnum
                  FROM (
                           SELECT * FROM WATER_TANK_ALARM_HISTORY
                           WHERE LINK_ID = #{link_id}
                           ORDER BY TANK_ID
                       ) a
              )
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}
